// ==UserScript==
// @name        Audiotool Theme [Local]
// @namespace   Violentmonkey Scripts
// @match       https://studio.audiotool.com/*
// @grant       none
// @version     0.2.4
// @author      sudo-matcha
// @require     https://code.jquery.com/jquery-3.7.1.min.js
// @require     https://raw.githubusercontent.com/brandonaaron/livequery/refs/heads/master/jquery.livequery.min.js
// @icon        https://www.audiotool.com/favicon.ico
// ==/UserScript==

const contextMenuColor = "rgba(255,255,255,0.07)";
const contextMenuBackdropFilter = "blur(15px) brightness(65%)";
const selColor = "rgba(39, 225, 251, 0.5)";
const atBlue = "rgb(39, 225, 251)";
const panelBgColor = "rgba(32, 33, 37, 1)"
const panelColor = "rgba(0,0,0,0)";
const catGif = "data:image/gif;base64,"
// Inject the animation keyframes into the page
const styleSheet = document.createElement("style");
styleSheet.textContent  = `
  @keyframes gradientScroll {
    0% { background-position: 0% 50%; }
    100% { background-position: 100% 50%; }
  }

  @keyframes bgGradientScroll {
    0% {
      background-position: 43% 0%;
      /* transform: rotateX(0deg) rotateY(0deg) rotateZ(0deg) scale3d(1,1,500); */
      }
    50% {
      background-position: 58% 100%;
      /* transform: rotateX(180deg) rotateY(90deg) rotateZ(110deg) scale3d(1,1,500); */
      }
    100% {
      background-position: 43% 0%;
      /* transform: rotateX(360deg) rotateY(0deg) rotateZ(0deg) scale3d(1,1,500); */
      }
  }

  @keyframes hoverGlowAtBlue {
    0%   { box-shadow: 0px 0px 5px rgba(39, 225, 251, 0.0); }
    100% { box-shadow: 0px 0px 8px rgba(39, 225, 251, 0.8); }
  }

  @keyframes hoverGlowWhite {
    0%   { text-shadow: 0px 0px 0px rgba(255, 255, 255, 0.0); }
    100% { text-shadow: 0px 0px 3px rgba(255, 255, 255, 0.8); }
  }

  @keyframes menuAnim {
    0% { transform: scale(0.8); }
    100% { transform: scale(1); }
  }

  @keyframes toastAnim {
    0% { transform: translateY(-100px); }
    100% { transform: translateY(0px); }
  }

  .start-dialog nav .button:hover {
    color: rgb(255, 255, 255);
    /* text-shadow: 0px 0px 2px rgba(255,255,255,0.3) */
    /* background-color: ${atBlue}; */
    box-shadow: 0px 0px 8px rgba(39, 225, 251, 0.8);
    animation: hoverGlowAtBlue 0.25s ease;
    background-color: rgb(0,0,0,0);
  }

  nav.menu div.container {
    animation: menuAnim 0.1s ease forwards;
  }

  div.dialog label.entry:hover {
    /* text-shadow: 0px 0px 4px rgba(255, 255, 255, 0.4) */
    animation: hoverGlowWhite 0.25s ease forwards;
  }

  div.toast {
    background-color: ${atBlue};
    box-shadow: 0px 0px 5px rgba(39, 225, 251, 0.7);
    animation: toastAnim 0.2s cubic-bezier(0.175, 0.885, 0.32, 1.1);
  }

  input[type="range"] {
    height: 15px;
    border-radius: 15px;
  }

  input[type="range"]::-webkit-slider-thumb {
    background: ${atBlue}
  }

  main.studio {
    /* background: linear-gradient(225deg, rgba(54, 54, 54, 0.05), rgba(39, 225, 251, 0.05)); */
    /* background-size: 800% 800%; */

    /* animation: bgGradientScroll 51s ease infinite; */
    /* background-image: url("https://myremix.ai/starfield.mp4") */
  }

  main.studio > article > section.editors > div.desktop-wrapper.await:before,
  main.studio > article > section.editors > div.timeline-wrapper.await:before {
    content: " ";
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    position: absolute;
    box-shadow: 0 0 2px 2px ${atBlue} inset;
    border-radius: 4px;
    z-index: 9999;
    pointer-events: none;
  }

`;
document.head.appendChild(styleSheet);

$(document).livequery("div.meta", (meta) => {
  meta.style.backgroundColor = contextMenuColor;
  meta.style.backdropFilter = contextMenuBackdropFilter;
});

$(document).livequery("nav.menu", (e) => {
  e.style.backgroundColor = contextMenuColor;
  e.style.backdropFilter = contextMenuBackdropFilter;
  e.style.color = "rgb(255,255,255)"
  e.style.borderRadius = "10px"
  // e.style.animation = "menuAnim 1s ease forwards"
  $(e).livequery(
    "div.selected",
    (sel) => {
      sel.style.backgroundColor = selColor;
    },
    (sel) => {
      sel.style.backgroundColor = "rgba(0,0,0,0)";
    }
  );
});

$(document).livequery("div.toast", (toast) => {
    toast.style.backgroundColor = atBlue;
    toast.style.boxShadow = "0px 0px 5px rgba(39, 225, 251, 0.7)";
});

$(document).livequery("div.sample.selected", (sample) => {
  sample.style.color = "#FFF"
  sample.style.textShadow = "0px 0px 3px rgba(255, 255, 255, 0.3)"
  sample.style.backgroundColor = "rgba(255, 255, 255, 0.1)"
}, (unsel) => {
  unsel.setAttribute("style", "")
});

$(document).livequery("button.default.selected", (sel) => {
  sel.style.textShadow = "0px 0px 3px rgba(255, 255, 255, 0.3)"
}, (unsel) => {
  unsel.style.textShadow = "none"
});

$(document).livequery(".progress > span, .progress .running span", (progressBar) => {
    progressBar.style.filter = "hue-rotate(180deg)"
});

$(document).livequery("div.dialog", (dialog) => {
  dialog.style.backgroundColor = contextMenuColor;
  dialog.style.backdropFilter = contextMenuBackdropFilter;
  dialog.style.borderRadius = "10px"
  Array.from(dialog.querySelectorAll("input, textarea, div.editable-tags")).forEach((e) => {
    e.style.borderRadius = "3px";
    e.style.backgroundColor = contextMenuColor;
    e.style.backdropFilter  = contextMenuBackdropFilter;
  });
  Array.from(dialog.querySelectorAll("h1")).forEach((e) => {
    e.style.textShadow = "0px 0px 3px rgba(255,255,255,0.5)";
  });
  $(dialog).livequery(".progress > span", (progressBar) => {
    progressBar.style.filter = "hue-rotate(180deg)"
  });
  $(dialog).livequery("button", (button) => {
    button.style.borderRadius = "3px"
  });
  $(dialog).livequery("label.alert", (label) => {
    label.style.color = atBlue;
  });
});

$(document).livequery("section.desktop", (sec) => {
  let context = sec.querySelector("div.desktop-context")
  context.style.backgroundColor = "rgba(0,0,0,0)";
  Array.from(context.getElementsByTagName("svg")).forEach((svg) => {
    svg.style.filter = "drop-shadow( 0px 0px 2px rgba(0, 0, 0, .9))"
  });
});

$(document).livequery("main.studio", (main) => {
  main.style.backgroundColor = panelBgColor;
  main.style.backgroun = "url('https://myremix.ai/starfield.mp4')"
  main.querySelector("section.controls").style.backgroundColor = "rgba(0,0,0,0)";
  main.querySelector("section.peak-meter").style.backgroundColor = "rgba(0,0,0,0)";
  main.querySelector("div.header").childNodes.forEach((e) => {
    e.style.backgroundColor = panelColor;
  });
  main.querySelector("header").childNodes.forEach((e) => {
    e.style.backgroundColor = panelColor;
  });
  main.querySelector("aside.panels").childNodes.forEach((e) => {
    e.style.backgroundColor = panelColor;
  });
  main.querySelector("footer").childNodes.forEach((e) => {
    e.style.backgroundColor = panelColor;
  });

  $(main).livequery("div.notification", (notif) => {
    notif.style.backgroundColor = contextMenuColor;
    notif.style.backdropFilter = contextMenuBackdropFilter;
    notif.style.borderRadius = "7px"
    Array.from(notif.querySelectorAll("h1, h2, h3, h4")).forEach((heading) => {
      heading.style.color = atBlue;
    });
    Array.from(notif.querySelectorAll("button")).forEach((button) => {
      if(!Array.from(button.getElementsByTagName("svg"))){
        button.style.color = "rgba(46,46,46,1)";
      }
      button.style.backgroundColor = atBlue;
      button.style.borderRadius = "3px"
      // button.style.border = "0.5px solid rgb(150,150,150)"
    });
  });

  // Apply the scrolling gradient to footer > section.info using a pseudo-element
  const infoSection = main.querySelector("footer section.info");
  infoSection.style.position = "relative";
  infoSection.style.color = "transparent"; // Hide the original text color

  const gradientStyle = `
    footer section.info::after {
      content: attr(data-text); /* Use the same text as the original element */
      background: linear-gradient(90deg, #ff7a7a, #ffd17a, #7aff7a, #7affff, #7a7aff);
      background-size: 200% 100%;
      background-clip: text;
      -webkit-background-clip: text;
      color: transparent;
      animation: gradientScroll 30s ease infinite;
      text-align: center;
      display: inline-block;
    }
  `;

  const gradientStyleSheet = document.createElement("style");
  gradientStyleSheet.textContent = gradientStyle;
  document.head.appendChild(gradientStyleSheet);

  // Copy the text into the `data-text` attribute for use in the pseudo-element
  infoSection.setAttribute("data-text", infoSection.textContent);
  let catGifEle = document.createElement("img")
  catGifEle.setAttribute("src", `${catGif}`)
  catGifEle.style.margin = "3px 0px 0px 5px";
  catGifEle.style.height = "15px";
  catGifEle.style.width = "auto";
  catGifEle.style.position = "absolute";
  catGifEle.style.left = "0px";
  catGifEle.style.transform = "scaleX(-1)";
  infoSection.append(catGifEle)
});

$(document).livequery("header", (header) => {
  header.childNodes.forEach((e) => {
    e.style.backgroundColor = panelColor;
    // let userAvatarImg;
    // for (let child of e.childNodes) {
    //   if (Array.from(child.classList).includes("user-avatar")) {
    //     userAvatarImg = child;
    //     break;
    //   }
    // }
    // if (userAvatarImg) {
    //   userAvatarImg.style.borderRadius = "5px";
    //   userAvatarImg.style.margin = "5px";
    // }
  });
});

$(document).livequery("button.default.selected", (buttonSel) => {
  buttonSel.style.textShadow = "0px 0px 3px rgba(255,255,255,0.5)"
}, (buttonUnsel) => {
  buttonUnsel.style.textShadow = "none"
});

